  handleCheck() {
    this.form().submit(async v => {
      const params = {
        input: {
          name: v?.name,
          displayName: v?.displayName,
          namespace: this.props?.project,
          description: v?.description,
          ossinput: {
            bucket: v?.bucket,
            object: v?.object,
          },
          endpointinput: {
            url: v?.serverAddress,
            insecure: v?.insecure === 'https' ? false : true,
            auth: {
              rootUser: v?.username,
              rootPassword: v?.password,
            },
          },
        },
      };
      try {
        const res = await this.props?.bff?.checkDatasource(params);
        if (res?.Datasource?.checkDatasource?.status === 'True') {
          this.setState({
            checked: true,
          });
        } else {
          this.setState({
            checked: false,
          });
        }
      } catch (error) {
        this.setState({
          checked: false,
          checkedMessage: error?.response?.errors?.[0]?.message,
        });
      }
    });
  }

  handleResetChecked() {
    this.setState({
      checked: undefined,
    });
  }

  handleSave() {
    this.form().submit(async v => {
      this.props.handleSave && this.props.handleSave(v);
    });
  }

